mapper.update:
update favorite_blog_group


        cover = #{info.cover},


        name = #{info.name},


        open = #{info.open},


        description = #{info.description}


where id = #{info.groupId}
and user_id = #{userId}

mapper.select:
select count(*)
from favorite_blog_group
where user_id = #{userId}
  and name = #{name}
